import React from 'react';
import './Hello.css';
import 'tachyons';

const Hello = ({ name, email, id }) => {
  return (
    <div className='tc'>
      <div className='flex items-center'>
        <img
          className='ba dib mb4 br-100 h3 w3 tc'
          src={`https://robohash.org/${id}?200x200`}
          alt={name}
          width={50}
        />
        {(name || email) && <p>My name is {name} and my email is {email}</p>}
      </div>
    </div>
  );
};

export default Hello;
