import React from 'react';
import 'tachyons'; // Assuming you want to use Tachyons for styling, make sure it's installed in your project.

const RoundedImageWithText = ({ imageUrl, text }) => {
  return (
    <div className="flex items-center">
      <div className="w3 h3 mr3 overflow-hidden br-100">
        {/* Assuming you want the image to be a circle. Adjust dimensions and styling as needed. */}
        <img
          src={imageUrl}
          alt="User"
          className="w-100 h-100 object-cover"
        />
      </div>
      <p>{text}</p>
    </div>
  );
};

export default RoundedImageWithText;
